import { useState, useEffect } from 'react';
import '../styles/Gallery.css';

const galleryCategories = [
  { id: 'all', name: 'All Photos' },
  { id: 'watches', name: 'Watches' },
  { id: 'stores', name: 'Stores' },
  { id: 'craftsmanship', name: 'Craftsmanship' },
  { id: 'events', name: 'Events' }
];

// All available images from public/images folder
const allImages = [
  '/images/1.jpg', '/images/2.jpg', '/images/3.jpg', '/images/4.jpg', '/images/5.jpg',
  '/images/6.jpg', '/images/7.jpg', '/images/8 (2).jpg', '/images/9.jpg', '/images/10.jpg',
  '/images/11.jpg', '/images/12.jpg', '/images/13.jpg', '/images/14.jpg', '/images/15.jpg',
  '/images/16.jpg', 'data:image/jpeg;base64,', 'data:image/jpeg;base64,', 'data:image/jpeg;base64,', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSmMmMpaP-YDQG66ltDvVUD0tVL_Ndj3QzShxXUeZ5fLe30kBoZZtpMJHI7Zj4vDForOKQ&usqp=CAU',
  'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSulzoPSbr9SN_hK8V_7pXxclxQaP0dgjN7sG8KPpgwYrU57d0bFGBFJATX3uMDrVnbaxI&usqp=CAU', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSmYinqYWrpZ6PG0CDiFvV4VNleWc65XXIv9Q&s', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTXtIkMcHH9nrwkBm0vABs_7P86Pco3bS3EYTypQBOEmlbsVVvuLgaRRFWqka7gInSLvQg&usqp=CAU', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTrGLHlYbKJbnnwbm6mnZSGi--V9dD9H1U70A&s', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTOAmyFPXPSHJVAoZ9RO08vd5_xijFDKB3M8jsEEET2BJ-MTUoIvYY-mq0gg4Dc1p6VHbg&usqp=CAU',
  'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ-xwV3XkByBUUpaoVOBGtDGDwbHZt2cQ_4gA&s'
];

// Generate gallery images using local images
const generateGalleryImages = () => {
  const galleryImages = [];
  let id = 1;

  // Watches category - use all watch images
  const watchCaptions = [
    'Vintage Rolex Collection',
    'Luxury Timepiece Display',
    'Diamond-Encrusted Collection',
    'Smart Watch Collection',
    'Heritage Collection',
    'Premium Watch Showcase',
    'Automatic Watch Collection',
    'Chronograph Timepieces',
    'Classic Mechanical Watches',
    'Modern Sports Watches',
    'Elegant Dress Watches',
    'Swiss Made Collection',
    'Leather Strap Collection',
    'Gold & Platinum Series',
    'Vintage Pocket Watches',
    'Digital Smart Watches'
  ];
  watchCaptions.forEach((caption, index) => {
    galleryImages.push({
      id: id++,
      src: allImages[index % allImages.length],
      caption,
      category: 'watches'
    });
  });

  // Stores category - cycle through images
  const storeCaptions = [
    'Manhattan Flagship Store',
    'Beverly Hills Boutique',
    'Luxury Watch Boutique Interior'
  ];
  storeCaptions.forEach((caption, index) => {
    galleryImages.push({
      id: id++,
      src: allImages[(index + 16) % allImages.length],
      caption,
      category: 'stores'
    });
  });

  // Craftsmanship category - cycle through images
  const craftCaptions = [
    'Master Watchmaker at Work',
    'Precision Assembly',
    'Watch Restoration Process',
    'Expert Watch Repair'
  ];
  craftCaptions.forEach((caption, index) => {
    galleryImages.push({
      id: id++,
      src: allImages[(index + 19) % allImages.length],
      caption,
      category: 'craftsmanship'
    });
  });

  // Events category - cycle through images
  const eventCaptions = [
    'Annual Watch Exhibition',
    'Limited Edition Launch',
    'VIP Private Viewing'
  ];
  eventCaptions.forEach((caption, index) => {
    galleryImages.push({
      id: id++,
      src: allImages[(index + 23) % allImages.length],
      caption,
      category: 'events'
    });
  });

  return galleryImages;
};

const galleryImages = generateGalleryImages();

export default function GalleryPage() {
  const [selectedImg, setSelectedImg] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState('all');

  const filteredImages = selectedCategory === 'all' 
    ? galleryImages 
    : galleryImages.filter(img => img.category === selectedCategory);

  useEffect(() => {
    if (selectedImg) {
      // Save current scroll position
      const scrollY = window.scrollY;
      // Block scrolling
      document.body.style.position = 'fixed';
      document.body.style.top = `-${scrollY}px`;
      document.body.style.width = '100%';
      document.body.style.overflow = 'hidden';

      // Cleanup: restore scrolling when modal closes
      return () => {
        document.body.style.position = '';
        document.body.style.top = '';
        document.body.style.width = '';
        document.body.style.overflow = '';
        window.scrollTo(0, scrollY);
      };
    }
  }, [selectedImg]);

  return (
    <div className="gallery-page">
      <div className="gallery-container">
        <div className="gallery-header">
          <h1 className="gallery-title">Photo Gallery</h1>
          <p className="gallery-intro">
            Explore our collection, craftsmanship, and store ambiance through our curated gallery.
            From vintage timepieces to modern masterpieces, discover the artistry behind every watch.
          </p>
        </div>

        <div className="gallery-filters">
          {galleryCategories.map(cat => (
            <button
              key={cat.id}
              className={`filter-btn ${selectedCategory === cat.id ? 'active' : ''}`}
              onClick={() => setSelectedCategory(cat.id)}
            >
              {cat.name}
            </button>
          ))}
        </div>

        <div className="gallery-grid">
          {filteredImages.map((img, index) => (
            <div
              key={img.id}
              className="gallery-item"
              onClick={() => setSelectedImg(img)}
              style={{ animationDelay: `${index * 0.05}s` }}
            >
              <img 
                src={img.src} 
                alt={img.caption} 
                className="gallery-thumb"
                loading="lazy"
              />
              <div className="gallery-overlay">
                <span className="gallery-caption">{img.caption}</span>
                <span className="view-icon">üëÅÔ∏è View</span>
              </div>
            </div>
          ))}
        </div>

        {selectedImg && (
          <div className="gallery-lightbox" onClick={() => setSelectedImg(null)}>
            <div className="lightbox-content" onClick={e => e.stopPropagation()}>
              <button className="lightbox-close" onClick={() => setSelectedImg(null)}>√ó</button>
              <img src={selectedImg.src} alt={selectedImg.caption} className="lightbox-image" />
              <p className="lightbox-caption">{selectedImg.caption}</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
